import { Home } from "../views";
import ProtectedRoute from "../components/protected-route";

export const RedirecttUrl = () => {
  const History = useHistory();  
  const { user } = useAuth0();
  const [checked, setChecked] = useState(true);
  const [loading, setLoading] = useState(false);

  fetch("http://localhost:8080/api/v1/Users/send",{
    method:"POST",
    body:JSON.stringify({email:user?.email,btn:true}),
 headers:{"Content-Type":"application/json"}
  }).then((res) => res.json())
  .then(data => {
    if (!data) {              
        console.log(data);
        fetch("http://localhost:8080/api/v1/Users",{
            method:"POST",
            body:JSON.stringify({email:user.email,btn:false}),
        headers:{"Content-Type":"application/json"}
        }).then((res) => res.json())
        .then(data => {
            if (data) {              
            console.log(data);
            }
            else {              
            alert('ERROR')
            }
        })
    }
    else {            
            fetch(`http://localhost:8080/api/v1/btn/${user.email}`, {  //getting button value from database
              method: "GET",
            }).then((res) => res.json())
              .then(data => {
                if(data)
                {
                  setChecked(data);
                }
                else 
                {
                  setChecked(false);
                }
                setLoading(true);
              })
    }
  })        
          
          if(loading && checked)
          {
            // let redirect = checked ?  : "/";
            History.push(`otpview`) 
          }
          else
          {
            History.push(``) 
          }
  return (
    
    <div className="App">
      <h1>LOADING....</h1>      
    </div>

  );
};


export default RedirecttUrl;
