import { StrictMode } from "react";
import OtpView from "../otp/otp";
import React from "react";
import { useAuth0 } from "@auth0/auth0-react";

const OTP = () => {
  const { user } = useAuth0();
  let email=user.email;
  fetch(`http://localhost:8080/api/v1/btn/${email}`,{  //getting button value from database
    method:"GET",
    }).then((res) => res.json()) 
    .then(data => {
      if (data!=null) {              
        checked=data; 
      }
      else
      {
        checked=false;
      }
      setChecked(checked);
    })
    return(
        // <StrictMode>
        checked &&
          <OtpView />
        // </StrictMode>
    )
  };

export default OTP;
