import React from 'react';
import { useHistory } from 'react-router-dom';
import {Auth0Provider } from '@auth0/auth0-react';
import { useAuth0 } from "@auth0/auth0-react";
import { useState } from "react";

const Auth0ProviderWithHistory = ({children}) => {
    const { user } = useAuth0();
  // let email=user.email;
let [checked, setChecked] = useState(null);
fetch("http://localhost:8080/api/v1/btn/subhusubhash24@gmail.com",{  //getting button value from database
  method:"GET",
  }).then((res) => res.json()) 
  .then(data => {
    if (data==true) {              
      checked=true; 
    }
    else
    {
      checked=false;
    }
    setChecked(checked);
  })
    console.log(checked);
    const history = useHistory();
    const domain = process.env.REACT_APP_AUTH0_DOMAIN;
    const clientId = process.env.REACT_APP_AUTH0_CLIENT_ID;
    const audience = process.env.REACT_APP_AUTH0_AUDIENCE;
    const redirect= checked ? 'http://localhost:3000/otpview' : 'http://localhost:3000';
    console.log(redirect);
    const onRedirectCallback = (appState) => {
        history.push(appState?.returnTo || window.location.pathname);
    };

    return(
        <Auth0Provider
            domain = {domain}
            clientId = {clientId}
            // redirectUri={`http://localhost:3000/otpview`}
            redirectUri={redirect}
            onRedirectCallback = {onRedirectCallback}
            audience={audience}
        >
            {children}
        </Auth0Provider>    
    );
};

export default Auth0ProviderWithHistory;
