// import OtpInput from "react-otp-input";
// import { useRef } from "react";
import React, { useEffect, useState } from "react";
import { Stack } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import { Home } from "../views";
import ProtectedRoute from "../components/protected-route";

const OtpView = () => {
  const History = useHistory();  
  const { user } = useAuth0();
  let email=user.email;
  var otp1;

  let [checked, setChecked] = useState(null);
  fetch(`http://localhost:8080/api/v1/btn/${email}`,{
    method:"GET",
    }).then((res) => res.json()) 
    .then(data => {
      if (data!=null) {              
        checked=data; 
      }
      else
      {
        checked="";
      }
      setChecked(checked);
    })
  // const handleOtp = () => {     
  //   document.getElementById('hide').style.display="block";

    fetch(`http://localhost:8080/api/v1/User/${email}`,{
        method:"GET",
        }).then((res) => res.json()) 
        .then(data => {
          console.log(data);
          if (data) {              
            fetch(`http://localhost:8080/api/v1/Users/${email}`,{
             method:"PUT",
             body:JSON.stringify({email:user.email}),
             headers:{"Content-Type":"application/json"}
             }).then(()=>{alert('OTP SENT')}) 
          }
          else {              
            fetch("http://localhost:8080/api/v1/Users",{
              method:"POST",
              body:JSON.stringify({email:user.email}),
              headers:{"Content-Type":"application/json"}
              }).then(()=>{alert('OTP SENT')}) 
          }
        })
        
  // };

  
  const HandleClick = () => { 
           otp1=document.getElementById('otp').value;
        fetch("http://localhost:8080/api/v1/Users/send",{
            method:"POST",
            body:JSON.stringify({email:user.email, otp:otp1}),
         headers:{"Content-Type":"application/json"}
          }).then((res) => res.json())
          .then(otp => {
            if (otp) {              
            console.log(otp);
              // window.open("http://localhost:4040");
              History.push("/")
              
              alert('LOGIN SUCCESSFUL')
              // this.props.navigation.navigate('/')
            }
            else {              
            alert('INAVLID OTP')
            }
          })
    

  };
 
  return (
    
    <div className="App">
      <h1>OTP VERIFICATION</h1>
      <h6>Enter the received otp below</h6>
      {/* <button onClick={handleOtp}>Send OTP</button> */}
      <div className="mx-auto" id="hide" style={{width: "200"}}>
      {/* ,display:"none" */}
        <input type="text" id="otp" name="otp" placeholder="Enter otp" required />
        <button onClick={HandleClick}>Submit</button>
      
      </div>
    </div>

  );
};


export default OtpView;
