// import OtpInput from "react-otp-input";
// import { useRef } from "react";
import React, { useEffect, useState } from "react";
import { Stack } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react";
import { Home } from ".";
import ProtectedRoute from "../components/protected-route";

export const RedirecttUrl = () => {
  const History = useHistory();  
  const { user } = useAuth0();
  const [checked, setChecked] = useState(true);
  const [loading, setLoading] = useState(false);

  fetch(`http://localhost:8080/api/v1/User/${user.email}`,{
    method:"GET",
//     body:JSON.stringify({email:user.email}),
//  headers:{"Content-Type":"application/json"}
  }).then((res) => res.json())
  .then(data => {
    console.log(data);
    if (data["message"]=="User not found for this id") {              
        console.log("First time");
        fetch("http://localhost:8080/api/v1/Users",{
            method:"POST",
            body:JSON.stringify({email:user.email,btn:true}),
        headers:{"Content-Type":"application/json"}
        }).then((res) => res.json())
        .then(data => {
            if (data) {              
            console.log(data);
            }
            else {              
            alert('ERROR')
            }
        })
    }
    else {       
      console.log("Second time");     
            fetch(`http://localhost:8080/api/v1/btn/${user.email}`, {  //getting button value from database
              method: "GET",
            }).then((res) => res.json())
              .then(data => {
                console.log(data);
                if(data)
                {
                  setChecked(data);
                }
                else 
                {
                  setChecked(false);
                }
                setLoading(true);
              })
              if(loading && checked)
              {
                console.log("right");
                // let redirect = checked ?  : "/";
                History.push("otpview") 
              }
              else
              {
                History.push("") 
              }
    }
  }) ;       
          
         
  return (
    
    <div className="App">
      <h1>LOADING....</h1>      
    </div>

  );
};


export default RedirecttUrl;
